import React, { useEffect, useState } from "react";
import API from "../../api/api";
import Navbar from "../Layout/Navbar";

export default function MarkAttendance() {
  const [classes, setClasses] = useState([]);
  const [students, setStudents] = useState([]);
  const [classId, setClassId] = useState("");
  const [date, setDate] = useState("");
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(false);

  // Load classes
  useEffect(() => {
    API.get("/classes").then((res) => setClasses(res.data));
  }, []);

  // Load students for selected class
  const loadStudents = async (cid) => {
    if (!cid) return;

    setClassId(cid);
    setStudents([]);
    setRecords([]);

    try {
      const res = await API.get(`/users?classId=${cid}&role=student`);
      setStudents(res.data);

      setRecords(
        res.data.map((s) => ({
          studentId: s._id,
          status: "Present"
        }))
      );
    } catch (err) {
      alert("Failed to load students");
    }
  };

  // Save attendance
  const submit = async () => {
    if (!classId) {
      alert("‚ùå Please select a class");
      return;
    }

    if (!date) {
      alert("‚ùå Please select a date");
      return;
    }

    if (records.length === 0) {
      alert("‚ùå No students available");
      return;
    }

    setLoading(true);

    try {
      await API.post("/attendance", {
        classId,
        date,
        records
      });

      alert("‚úÖ Attendance saved successfully");
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message ||
          "‚ùå Failed to save attendance"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />

      <div style={{ padding: 20 }}>
        <h2>üìã Mark Attendance</h2>

        {/* ===== CLASS SELECT ===== */}
        <select value={classId} onChange={(e) => loadStudents(e.target.value)}>
          <option value="">Select Class</option>
          {classes.map((c) => (
            <option key={c._id} value={c._id}>
              {c.name}
            </option>
          ))}
        </select>

        {/* ===== DATE SELECT ===== */}
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />

        {/* ===== STUDENT LIST ===== */}
        {students.map((s, i) => (
          <div key={s._id} style={styles.row}>
            <span>{s.name}</span>

            <select
              value={records[i]?.status}
              onChange={(e) =>
                setRecords((prev) =>
                  prev.map((rec, idx) =>
                    idx === i
                      ? { ...rec, status: e.target.value }
                      : rec
                  )
                )
              }
            >
              <option value="Present">Present</option>
              <option value="Absent">Absent</option>
            </select>
          </div>
        ))}

        {/* ===== SAVE BUTTON ===== */}
        <button
          className="primary-btn"
          onClick={submit}
          disabled={loading}
        >
          {loading ? "Saving..." : "Save Attendance"}
        </button>
      </div>
    </>
  );
}

const styles = {
  row: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: 8
  }
};
