import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Layout/Navbar";

export default function LiveRoom() {
  const { classId } = useParams();

  return (
    <>
      <Navbar />

      <div style={styles.container}>
        <h2>üé• Live Class Room</h2>

        <p>
          <strong>Class ID:</strong> {classId}
        </p>

        <div style={styles.videoBox}>
          <p>üî¥ Live Video Stream Area</p>
          <p>(WebRTC / Zoom integration comes here)</p>
        </div>

        <div style={styles.controls}>
          <button className="primary-btn">üé§ Mic</button>
          <button className="primary-btn">üì∑ Camera</button>
          <button className="secondary-btn">‚ùå Leave Class</button>
        </div>
      </div>
    </>
  );
}

const styles = {
  container: {
    padding: "20px",
    textAlign: "center"
  },
  videoBox: {
    margin: "20px auto",
    width: "80%",
    height: "350px",
    background: "#000",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "10px",
    flexDirection: "column"
  },
  controls: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    marginTop: "20px"
  }
};
