import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Layout/Navbar";

export default function LiveRoom() {
  const { classId } = useParams();
  const videoRef = useRef(null);
  const [isLive, setIsLive] = useState(false);

  // Start Camera
  const startLive = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      videoRef.current.srcObject = stream;
      setIsLive(true);
    } catch (err) {
      alert("Camera or microphone permission denied");
    }
  };

  // Stop Camera
  const stopLive = () => {
    const stream = videoRef.current.srcObject;
    const tracks = stream?.getTracks();
    tracks?.forEach(track => track.stop());
    videoRef.current.srcObject = null;
    setIsLive(false);
  };

  return (
    <>
      <Navbar />

      <div style={styles.container}>
        <h2>ðŸŽ¥ Live Class Room</h2>
        <p><strong>Class ID:</strong> {classId}</p>

        <div style={styles.videoBox}>
          {isLive ? (
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              style={styles.video}
            />
          ) : (
            <p>Camera is OFF</p>
          )}
        </div>

        <div style={styles.controls}>
          {!isLive ? (
            <button className="primary-btn" onClick={startLive}>
              â–¶ Start Camera
            </button>
          ) : (
            <button className="danger-btn" onClick={stopLive}>
              â›” Stop Camera
            </button>
          )}
        </div>

        <p style={{ marginTop: 15, color: "#555" }}>
          This live class uses WebRTC (getUserMedia) for real-time video streaming.
        </p>
      </div>
    </>
  );
}

const styles = {
  container: {
    padding: 30,
    textAlign: "center"
  },
  videoBox: {
    width: "80%",
    height: 350,
    margin: "20px auto",
    background: "#000",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 12
  },
  video: {
    width: "100%",
    height: "100%",
    borderRadius: 12,
    objectFit: "cover"
  },
  controls: {
    marginTop: 20
  }
};
