import React, { useEffect, useState } from "react";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function AddAssignment() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [classId, setClassId] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [classes, setClasses] = useState("");
  const [msg, setMsg] = useState("");

  /* ===== LOAD CLASSES ===== */
  useEffect(() => {
    API.get("/classes").then(res => setClasses(res.data));
  }, []);

  /* ===== SUBMIT ===== */
  const submit = async (e) => {
    e.preventDefault();

    if (!classId) {
      alert("Please select a class");
      return;
    }

    try {
      await API.post("/assignments", {
        title,
        description,
        classId,   // âœ… MUST be ObjectId
        dueDate
      });

      setMsg("âœ… Assignment added successfully");
      setTitle("");
      setDescription("");
      setClassId("");
      setDueDate("");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add assignment");
    }
  };

  return (
    <>
      <Navbar />

      <div style={styles.page}>
        <div style={styles.card}>
          <h2 style={styles.heading}>ðŸ“˜ Create Assignment</h2>

          <form onSubmit={submit} style={styles.form}>
            
            <input
              style={styles.input}
              placeholder="Assignment Title"
              value={title}
              onChange={e => setTitle(e.target.value)}
              required
            />

            <textarea
              style={styles.textarea}
              placeholder="Assignment Description"
              rows="4"
              value={description}
              onChange={e => setDescription(e.target.value)}
              required
            />

            <select
              style={styles.input}
              value={classId}
              onChange={e => setClassId(e.target.value)}
              required
            >
              <option value="">Select Class</option>
              {classes.map(cls => (
                <option key={cls._id} value={cls._id}>
                  {cls.name}
                </option>
              ))}
            </select>

            <input
              style={styles.input}
              type="date"
              value={dueDate}
              onChange={e => setDueDate(e.target.value)}
              required
            />

            <button style={styles.button}>âž• Add Assignment</button>

            {msg && <p style={styles.success}>{msg}</p>}
          </form>
        </div>
      </div>
    </>
  );
}

/* ===== STYLES ===== */
const styles = {
  page: {
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    paddingTop: 40,
    background: "#f8fafc"
  },
  card: {
    width: "100%",
    maxWidth: 520,
    background: "#ffffff",
    padding: 30,
    borderRadius: 14,
    boxShadow: "0 10px 30px rgba(0,0,0,0.08)"
  },
  heading: {
    textAlign: "center",
    marginBottom: 25
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: 14
  },
  input: {
    padding: 12,
    fontSize: 15,
    borderRadius: 8,
    border: "1px solid #cbd5e1"
  },
  textarea: {
    padding: 12,
    fontSize: 15,
    borderRadius: 8,
    border: "1px solid #cbd5e1",
    resize: "none"
  },
  button: {
    marginTop: 10,
    padding: 12,
    borderRadius: 8,
    border: "none",
    background: "#f4631e",
    color: "#fff",
    fontSize: 16,
    fontWeight: 600,
    cursor: "pointer"
  },
  success: {
    marginTop: 10,
    color: "green",
    textAlign: "center",
    fontWeight: 600
  }
};
