import React, { useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";
import Navbar from "../Layout/Navbar";

const socket = io("http://localhost:5000");

export default function TeacherLiveClass() {
  const { classId } = useParams();
  const videoRef = useRef(null);
  const peerRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    socket.emit("join-class", classId);
    startCamera();

    socket.on("answer", async (answer) => {
      await peerRef.current.setRemoteDescription(answer);
    });

    socket.on("ice-candidate", (candidate) => {
      peerRef.current.addIceCandidate(candidate);
    });

    return () => stopClass();
  }, []);

  const startCamera = async () => {
    streamRef.current = await navigator.mediaDevices.getUserMedia({
      video: true,
      audio: true
    });

    videoRef.current.srcObject = streamRef.current;

    peerRef.current = new RTCPeerConnection({
      iceServers: [{ urls: "stun:stun.l.google.com:19302" }]
    });

    streamRef.current.getTracks().forEach(track =>
      peerRef.current.addTrack(track, streamRef.current)
    );

    peerRef.current.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit("ice-candidate", {
          classId,
          candidate: e.candidate
        });
      }
    };

    const offer = await peerRef.current.createOffer();
    await peerRef.current.setLocalDescription(offer);

    socket.emit("offer", { classId, offer });
  };

  const stopClass = () => {
    streamRef.current?.getTracks().forEach(t => t.stop());
    peerRef.current?.close();
    socket.disconnect();
  };

  return (
    <>
      <Navbar />
      <h2 style={{ textAlign: "center" }}>ðŸŽ¥ Teacher Live Class</h2>
      <video
        ref={videoRef}
        autoPlay
        muted
        playsInline
        style={{ width: "80%", margin: "auto", display: "block" }}
      />
      <div style={{ textAlign: "center", marginTop: 20 }}>
        <button className="danger-btn" onClick={stopClass}>
          â›” End Class
        </button>
      </div>
    </>
  );
}
