import React, { useEffect, useState } from "react";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function ViewAttendance() {
  const [attendance, setAttendance] = useState([]);
  const [summary, setSummary] = useState(null);
useEffect(() => {
  API.get("/attendance/percentage").then(res => setSummary(res.data));
}, []);
{summary && (
  <div style={styles.summary}>
    Attendance: <strong>{summary.percentage}%</strong>
  </div>
)}


  useEffect(() => {
    API.get("/attendance").then(res => setAttendance(res.data));
  }, []);

  return (
    <>
      <Navbar />
      <div style={{ padding: 20 }}>
        <h2>ðŸ“Š Attendance Report</h2>

        {attendance.map((a) => (
          <div key={a._id} style={styles.card}>
            <strong>{a.studentId.name}</strong> | {a.classId.name} | {a.date}
            <span
              style={{
                marginLeft: 10,
                color: a.status === "Present" ? "green" : "red"
              }}
            >
              {a.status}
            </span>
          </div>
        ))}
      </div>
    </>
  );
}

const styles = {
  card: {
    padding: 10,
    background: "#e0f2fe",
    marginBottom: 8,
    borderRadius: 6
  }
};
