import React, { useState } from "react";
import Navbar from "../Layout/Navbar";
import "./LiveClass.css";

export default function TeacherLiveClass() {
  const [className, setClassName] = useState("");
  const [started, setStarted] = useState(false);

  const startClass = () => {
    if (!className) {
      alert("Please enter class name");
      return;
    }
    setStarted(true);
  };

  return (
    <>
      <Navbar />

      <div className="live-container">
        <h2>ðŸŽ¥ Teacher Live Class</h2>

        {!started ? (
          <div className="live-card">
            <input
              placeholder="Enter Class Name"
              value={className}
              onChange={(e) => setClassName(e.target.value)}
            />

            <button className="primary-btn" onClick={startClass}>
              Start Live Class
            </button>
          </div>
        ) : (
          <div className="live-room">
            <h3>Live Class: {className}</h3>

            <div className="video-box">
              ðŸŽ¥ Teacher Camera Stream
            </div>

            <div className="controls">
              <button className="secondary-btn">Mute</button>
              <button className="secondary-btn">Stop Video</button>
              <button className="danger-btn">End Class</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
