import React, { useEffect, useState } from "react";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function ViewAssignments() {
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [classId, setClassId] = useState("");

  const fetchAssignments = async (cls = "") => {
    setLoading(true);
    try {
      const res = await API.get(
        cls ? `/assignments?classId=${cls}` : "/assignments"
      );
      setAssignments(res.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to load assignments");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAssignments();
  }, []);

  return (
    <>
      <Navbar />

      <div style={{ padding: "20px" }}>
        <h2>ðŸ“˜ Assignments</h2>

        {/* ===== Filter ===== */}
        <div style={{ marginBottom: "15px" }}>
          <select
            value={classId}
            onChange={(e) => {
              setClassId(e.target.value);
              fetchAssignments(e.target.value);
            }}
          >
            <option value="">All Classes</option>
            <option value="class1">Class 1</option>
            <option value="class2">Class 2</option>
          </select>
        </div>

        {/* Loading */}
        {loading && <p>Loading assignments...</p>}

        {/* Error */}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {/* No Data */}
        {!loading && !error && assignments.length === 0 && (
          <p>No assignments found.</p>
        )}

        {/* Assignment Cards */}
        {assignments.map((a) => (
          <div key={a._id} style={styles.card}>
            <h4>ðŸ“„ {a.title}</h4>
            <p>{a.description}</p>

            <div style={styles.meta}>
              {a.dueDate && (
                <small>
                  ðŸ“… Due: {new Date(a.dueDate).toLocaleDateString()}
                </small>
              )}

              <span
                style={{
                  ...styles.status,
                  background:
                    a.status === "Submitted" ? "#bbf7d0" : "#fecaca",
                  color: a.status === "Submitted" ? "green" : "red"
                }}
              >
                {a.status || "Pending"}
              </span>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}

const styles = {
  card: {
    padding: "15px",
    marginBottom: "14px",
    background: "#e0f2fe",
    borderRadius: "10px",
    borderLeft: "5px solid #f4631e"
  },
  meta: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "10px",
    alignItems: "center"
  },
  status: {
    padding: "4px 10px",
    borderRadius: "20px",
    fontSize: "12px",
    fontWeight: "600"
  }
};
