import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import API from "../../api/api";

export default function AssignmentSubmissions() {
  const { assignmentId } = useParams();
  const [subs, setSubs] = useState([]);

  useEffect(() => {
    API.get(`/assignments/${assignmentId}/submissions`)
      .then(res => setSubs(res.data));
  }, []);

  const grade = async (id, marks, feedback) => {
    await API.put(`/assignments/grade/${id}`, { marks, feedback });
    alert("Graded");
  };

  return (
    <div style={{ padding: 30 }}>
      <h2>Submissions</h2>

      {subs.map(s => (
        <div key={s._id} style={{ background: "#e0f2fe", padding: 10 }}>
          <b>{s.studentId.name}</b>
          <p>{s.answer}</p>

          <input placeholder="Marks"
            onBlur={e => grade(s._id, e.target.value, s.feedback)} />

          <input placeholder="Feedback"
            onBlur={e => grade(s._id, s.marks, e.target.value)} />
        </div>
      ))}
    </div>
  );
}
