import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function AssignmentSubmissions() {
  const { assignmentId } = useParams();
  const [submissions, setSubmissions] = useState([]);
  const [marks, setMarks] = useState({});

  useEffect(() => {
    API.get(`/assignments/${assignmentId}/submissions`)
      .then(res => setSubmissions(res.data))
      .catch(() => alert("Failed to load submissions"));
  }, [assignmentId]);

  const grade = async (submissionId) => {
    try {
      await API.post(`/assignments/grade/${submissionId}`, {
        marks: marks[submissionId]
      });

      alert("âœ… Marks saved");
    } catch {
      alert("Failed to grade");
    }
  };

  return (
    <>
      <Navbar />

      <div style={{ padding: 30 }}>
        <h2>ðŸ“¥ Assignment Submissions</h2>

        {submissions.length === 0 && <p>No submissions yet.</p>}

        {submissions.map((s) => (
          <div key={s._id} style={styles.card}>
            <h4>ðŸŽ“ {s.studentId.name}</h4>
            <p>{s.answer}</p>

            <input
              type="number"
              placeholder="Marks"
              value={marks[s._id] || ""}
              onChange={(e) =>
                setMarks({ ...marks, [s._id]: e.target.value })
              }
            />

            <button
              className="secondary-btn"
              onClick={() => grade(s._id)}
            >
              Save Marks
            </button>
          </div>
        ))}
      </div>
    </>
  );
}

const styles = {
  card: {
    padding: 15,
    marginBottom: 15,
    background: "#f1f5f9",
    borderRadius: 8
  }
};
