import React, { useRef, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Layout/Navbar";

export default function LiveRoom() {
  const { classId } = useParams();
  const videoRef = useRef(null);
  const [isLive, setIsLive] = useState(false);
  const [error, setError] = useState("");

  /* ===============================
     START CAMERA
  =============================== */
  const startLive = async () => {
    setError("");

    try {
      // Browser support check
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        setError("âŒ Your browser does not support camera access");
        return;
      }

      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: "user" },
        audio: true
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }

      setIsLive(true);
    } catch (err) {
      console.error("Camera Error:", err);

      if (err.name === "NotAllowedError") {
        setError("âŒ Permission denied. Please allow camera & microphone.");
      } else if (err.name === "NotFoundError") {
        setError("âŒ No camera or microphone found.");
      } else if (err.name === "NotReadableError") {
        setError("âŒ Camera is already in use by another app.");
      } else {
        setError("âŒ Unable to access camera.");
      }
    }
  };

  /* ===============================
     STOP CAMERA
  =============================== */
  const stopLive = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsLive(false);
  };

  return (
    <>
      <Navbar />

      <div style={styles.container}>
        <h2>ðŸŽ¥ Live Class Room</h2>
        <p><strong>Class ID:</strong> {classId}</p>

        <div style={styles.videoBox}>
          {isLive ? (
            <video
              ref={videoRef}
              autoPlay
              playsInline
              muted
              style={styles.video}
            />
          ) : (
            <p>Camera is OFF</p>
          )}
        </div>

        {error && <p style={{ color: "red" }}>{error}</p>}

        <div style={styles.controls}>
          {!isLive ? (
            <button className="primary-btn" onClick={startLive}>
              â–¶ Start Camera
            </button>
          ) : (
            <button className="danger-btn" onClick={stopLive}>
              â›” Stop Camera
            </button>
          )}
        </div>

        <p style={{ marginTop: 15, color: "#555" }}>
          Live streaming powered by WebRTC (getUserMedia API)
        </p>
      </div>
    </>
  );
}

/* ===============================
   STYLES
=============================== */
const styles = {
  container: {
    padding: 30,
    textAlign: "center"
  },
  videoBox: {
    width: "80%",
    height: 350,
    margin: "20px auto",
    background: "#000",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 12
  },
  video: {
    width: "100%",
    height: "100%",
    borderRadius: 12,
    objectFit: "cover"
  },
  controls: {
    marginTop: 20
  }
};
