import React, { useState } from "react";
import Navbar from "../Layout/Navbar";
import "./LiveClass.css";

export default function StudentLiveClass() {
  const [classCode, setClassCode] = useState("");
  const [joined, setJoined] = useState(false);

  const joinClass = () => {
    if (!classCode) {
      alert("Enter class name/code");
      return;
    }
    setJoined(true);
  };

  return (
    <>
      <Navbar />

      <div className="live-container">
        <h2>ðŸŽ“ Join Live Class</h2>

        {!joined ? (
          <div className="live-card">
            <input
              placeholder="Enter Class Name / Code"
              value={classCode}
              onChange={(e) => setClassCode(e.target.value)}
            />

            <button className="primary-btn" onClick={joinClass}>
              Join Class
            </button>
          </div>
        ) : (
          <div className="live-room">
            <h3>Joined Class: {classCode}</h3>

            <div className="video-box">
              ðŸŽ¥ Teacher Live Stream
            </div>

            <div className="controls">
              <button className="secondary-btn">Mute</button>
              <button className="secondary-btn">Raise Hand</button>
              <button className="danger-btn">Leave Class</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
