import React, { useEffect, useRef } from "react";
import socket from "../../socket";
import Navbar from "../Layout/Navbar";
import { useParams } from "react-router-dom";

export default function StudentLiveClass() {
  const videoRef = useRef(null);
  const peerRef = useRef(null);
  const { classId } = useParams();

  useEffect(() => {
    socket.emit("join-class", classId);

    peerRef.current = new RTCPeerConnection();

    peerRef.current.ontrack = (e) => {
      videoRef.current.srcObject = e.streams[0];
    };

    peerRef.current.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit("ice-candidate", {
          classId,
          candidate: e.candidate
        });
      }
    };

    socket.on("offer", async (offer) => {
      await peerRef.current.setRemoteDescription(offer);
      const answer = await peerRef.current.createAnswer();
      await peerRef.current.setLocalDescription(answer);

      socket.emit("answer", { classId, answer });
    });

    socket.on("ice-candidate", (candidate) => {
      peerRef.current.addIceCandidate(candidate);
    });

    return () => peerRef.current?.close();
  }, []);

  return (
    <>
      <Navbar />
      <h2 style={{ textAlign: "center" }}>ðŸŽ“ Student Live Class</h2>
      <video ref={videoRef} autoPlay playsInline style={styles.video} />
      <button onClick={() => window.history.back()} className="danger-btn">
        Leave Class
      </button>
    </>
  );
}

const styles = {
  video: {
    width: "80%",
    margin: "20px auto",
    display: "block",
    borderRadius: 10
  }
};
