import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function AssignmentSubmissions() {
  const { assignmentId } = useParams();
  const [subs, setSubs] = useState([]);

  useEffect(() => {
    API.get(`/assignments/${assignmentId}/submissions`)
      .then(res => setSubs(res.data));
  }, []);

  const grade = async (id, marks, feedback) => {
    await API.put(`/assignments/submission/${id}/grade`, {
      marks,
      feedback
    });
    alert("Graded");
  };

  return (
    <>
      <Navbar />
      <div style={{ padding: 30 }}>
        <h2>üìù Submissions</h2>

        {subs.map(s => (
          <div key={s._id} style={styles.card}>
            <h4>{s.studentId.name}</h4>

            <a href={`http://localhost:5000${s.fileUrl}`} download>
              üì• Download File
            </a>

            <input
              placeholder="Marks"
              onChange={e => s.marks = e.target.value}
            />
            <input
              placeholder="Feedback"
              onChange={e => s.feedback = e.target.value}
            />

            <button onClick={() => grade(s._id, s.marks, s.feedback)}>
              Submit Grade
            </button>
          </div>
        ))}
      </div>
    </>
  );
}

const styles = {
  card: {
    background: "#e0f2fe",
    padding: 15,
    marginBottom: 12,
    borderRadius: 10
  }
};
