import React, { useState } from 'react';
import API from '../../api/api';

export default function DoubtSolver(){
  const [q, setQ] = useState('');
  const [ans, setAns] = useState(null);
  async function ask(e){
    e.preventDefault();
    const res = await API.post('/ai/ask', { question: q });
    setAns(res.data.answer);
  }
  return (
    <div className='card'>
      <h3>AI Doubt Solver</h3>
      <form onSubmit={ask}>
        <textarea value={q} onChange={e=>setQ(e.target.value)} placeholder='Ask your doubt...'></textarea>
        <button>Ask</button>
      </form>
      {ans && <div className='answer'><strong>Answer:</strong><p>{ans}</p></div>}
    </div>
  )
}
