import React, { useEffect, useState } from "react";
import Navbar from "../Layout/Navbar";
import API from "../../api/api";

export default function ViewAssignments() {
  const [assignments, setAssignments] = useState([]);
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ðŸ”¹ Fetch Classes
  const fetchClasses = async () => {
    try {
      const res = await API.get("/classes");
      setClasses(res.data);
    } catch (err) {
      console.error("Class load error");
    }
  };

  // ðŸ”¹ Fetch Assignments (Dynamic)
  const fetchAssignments = async (classId = "") => {
    setLoading(true);
    try {
      const url = classId
        ? `/assignments?classId=${classId}`
        : "/assignments";

      const res = await API.get(url);
      setAssignments(res.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to load assignments");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClasses();
    fetchAssignments();
  }, []);

  return (
    <>
      <Navbar />

      <div style={{ padding: "20px" }}>
        <h2>ðŸ“˜ Assignments</h2>

        {/* ===== CLASS FILTER ===== */}
        <div style={{ marginBottom: "15px" }}>
          <select
            value={selectedClass}
            onChange={(e) => {
              setSelectedClass(e.target.value);
              fetchAssignments(e.target.value);
            }}
          >
            <option value="">All Classes</option>
            {classes.map((cls) => (
              <option key={cls._id} value={cls._id}>
                {cls.name}
              </option>
            ))}
          </select>
        </div>

        {/* ===== STATES ===== */}
        {loading && <p>Loading assignments...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {!loading && !error && assignments.length === 0 && (
          <p>No assignments found.</p>
        )}

        {/* ===== ASSIGNMENT CARDS ===== */}
        {assignments.map((a) => (
          <div key={a._id} style={styles.card}>
            <h4>ðŸ“„ {a.title}</h4>
            <p>{a.description}</p>

            <div style={styles.meta}>
              {a.dueDate && (
                <small>
                  ðŸ“… Due: {new Date(a.dueDate).toLocaleDateString()}
                </small>
              )}

              <span
                style={{
                  ...styles.status,
                  background:
                    a.status === "Submitted" ? "#bbf7d0" : "#fecaca",
                  color: a.status === "Submitted" ? "green" : "red"
                }}
              >
                {a.status || "Pending"}
              </span>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}

/* ===== STYLES ===== */
const styles = {
  card: {
    padding: "15px",
    marginBottom: "14px",
    background: "#e0f2fe",
    borderRadius: "10px",
    borderLeft: "5px solid #f4631e"
  },
  meta: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "10px",
    alignItems: "center"
  },
  status: {
    padding: "4px 10px",
    borderRadius: "20px",
    fontSize: "12px",
    fontWeight: "600"
  }
};
