import React, { useRef, useState } from "react";
import { io } from "socket.io-client";
import Navbar from "../Layout/Navbar";

const socket = io("http://localhost:5000");

export default function TeacherLiveClass() {
  const videoRef = useRef(null);
  const peerRef = useRef(null);
  const streamRef = useRef(null);
  const [isLive, setIsLive] = useState(false);

  const classId = "class1"; // replace with dynamic classId later

  const startClass = async () => {
    socket.emit("join-class", classId);

    // Get teacher camera
    const stream = await navigator.mediaDevices.getUserMedia({
      video: true,
      audio: true
    });

    streamRef.current = stream;
    videoRef.current.srcObject = stream;
    videoRef.current.muted = true;

    peerRef.current = new RTCPeerConnection();

    // Send tracks to students
    stream.getTracks().forEach(track =>
      peerRef.current.addTrack(track, stream)
    );

    peerRef.current.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit("offer", {
          classId,
          offer: peerRef.current.localDescription
        });
      }
    };

    const offer = await peerRef.current.createOffer();
    await peerRef.current.setLocalDescription(offer);

    socket.on("answer", async (answer) => {
      await peerRef.current.setRemoteDescription(answer);
    });

    setIsLive(true);
  };

  const stopClass = () => {
    streamRef.current?.getTracks().forEach(t => t.stop());
    peerRef.current?.close();
    videoRef.current.srcObject = null;
    setIsLive(false);
  };

  return (
    <>
      <Navbar />
      <h2>ðŸŽ¥ Teacher Live Class</h2>

      <video
        ref={videoRef}
        autoPlay
        playsInline
        width="700"
        style={{ borderRadius: 10, background: "#000" }}
      />

      <br /><br />

      {!isLive ? (
        <button className="primary-btn" onClick={startClass}>
          â–¶ Start Class
        </button>
      ) : (
        <button className="danger-btn" onClick={stopClass}>
          â›” Stop Class
        </button>
      )}
    </>
  );
}
