import React from "react";
import Navbar from "../Layout/Navbar";

export default function AdminDashboard() {
  return (
    <>
      <Navbar />

      <div style={styles.container}>
        <h2>Admin Dashboard</h2>

        <div style={styles.grid}>
          <div style={styles.card}>
            <h3>User Management</h3>
            <p>Add / remove students & teachers</p>
            <a className="action-btn" href="/users">Manage Users</a>
          </div>

          <div style={styles.card}>
            <h3>Add Class</h3>
            <p>Create & assign classes</p>
            <a className="action-btn secondary" href="/add-class">Add Class</a>
          </div>

          <div style={styles.card}>
            <h3>All Classes</h3>
            <p>View all classes</p>
            <a className="action-btn" href="/classes">View Classes</a>
          </div>

          <div style={styles.card}>
            <h3>Attendance Reports</h3>
            <p>Student attendance records</p>
            <a className="action-btn danger" href="/attendance">View Attendance</a>
          </div>
        </div>
      </div>
    </>
  );
}

const styles = {
  container: {
    padding: "20px"
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "20px",
    marginTop: "20px"
  },
  card: {
    background: "white",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
  }
};
