const mongoose = require("mongoose");

const SubmissionSchema = new mongoose.Schema({
  assignmentId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Assignment"
  },
  studentId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "User"
  },
  answer: String,
  marks: Number,
  feedback: String,
  status: {
    type: String,
    enum: ["Submitted", "Checked"],
    default: "Submitted"
  }
});

module.exports = mongoose.model("Submission", SubmissionSchema);
