import React, { useState } from "react";
import { Link } from "react-router-dom";
import HomeHeader from "./HomeHeader";
import Modal from "../Common/Modal";
import Login from "../Auth/Login";
import Register from "../Auth/Register";
import "./Home.css";

export default function Home() {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <>
      <HomeHeader
        onLogin={() => setShowLogin(true)}
        onSignup={() => setShowSignup(true)}
      />

      {/* ===== Hero Section ===== */}
      <section className="hero">
        <div className="hero-content">
          <h1>
            The Intelligent <span>Classroom</span> Era Begins
          </h1>

          <p>
            AI-powered school management system with smart classrooms,
            assignments, attendance, live classes and intelligent doubt solving.
          </p>

          <div className="hero-buttons">
            <button className="primary-btn" onClick={() => setShowSignup(true)}>
              Get Started
            </button>
            <button className="secondary-btn" onClick={() => setShowLogin(true)}>
              Login
            </button>
          </div>
        </div>
      </section>

      {/* ===== Role Section ===== */}
      <section className="features">
        <h2>Who Can Use AI School?</h2>

        <div className="features-grid">
          <div className="feature-card">
            <h3>üè´ Schools</h3>
            <p>Centralized admin dashboard for school management.</p>
            <Link to="/school" className="primary-btn">Explore School</Link>
          </div>

          <div className="feature-card">
            <h3>üë©‚Äçüè´ Teachers</h3>
            <p>Manage classes, assignments, attendance and live sessions.</p>
            <Link to="/teacher-info" className="primary-btn">Explore Teachers</Link>
          </div>

          <div className="feature-card">
            <h3>üéì Students</h3>
            <p>Attend classes, track attendance and ask AI doubts.</p>
            <Link to="/student-info" className="primary-btn">Explore Students</Link>
          </div>
        </div>
      </section>

      {/* ===== POPUPS ===== */}
      {showLogin && (
        <Modal onClose={() => setShowLogin(false)}>
          <Login />
        </Modal>
      )}

      {showSignup && (
        <Modal onClose={() => setShowSignup(false)}>
          <Register />
        </Modal>
      )}

      {/* ===== Footer ===== */}
      <footer className="footer">
        <p>¬© 2025 AI School Management System</p>
        <p>Developed for Academic Project</p>
      </footer>
    </>
  );
}
