import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import Navbar from "../Layout/Navbar";

const socket = io("http://localhost:5000");

export default function StudentLiveClass() {
  const videoRef = useRef(null);
  const peerRef = useRef(null);
  const [joined, setJoined] = useState(true);

  const classId = "class1"; // same classId as teacher

  useEffect(() => {
    socket.emit("join-class", classId);

    peerRef.current = new RTCPeerConnection();

    // Receive teacher stream
    peerRef.current.ontrack = (e) => {
      videoRef.current.srcObject = e.streams[0];
    };

    socket.on("offer", async (offer) => {
      await peerRef.current.setRemoteDescription(offer);
      const answer = await peerRef.current.createAnswer();
      await peerRef.current.setLocalDescription(answer);

      socket.emit("answer", {
        classId,
        answer
      });
    });

    return () => leaveClass();
  }, []);

  const leaveClass = () => {
    peerRef.current?.close();
    videoRef.current.srcObject = null;
    setJoined(false);
  };

  if (!joined) {
    return (
      <>
        <Navbar />
        <h2>ðŸ‘‹ You have left the class</h2>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <h2>ðŸ“º Live Class (Student)</h2>

      <video
        ref={videoRef}
        autoPlay
        playsInline
        controls
        width="700"
        style={{ borderRadius: 10, background: "#000" }}
      />

      <br /><br />

      <button className="danger-btn" onClick={leaveClass}>
        ðŸšª Leave Class
      </button>
    </>
  );
}
