import React, { useRef, useState } from "react";
import { io } from "socket.io-client";
import Navbar from "../Layout/Navbar";

const socket = io("http://localhost:5000");

export default function TeacherLiveClass() {
  console.log("TeacherLiveClass loaded");

  const videoRef = useRef(null);
  const [isLive, setIsLive] = useState(false);
  const [error, setError] = useState("");

  const classId = "class1"; // static for now

  const startClass = async () => {
    try {
      socket.emit("join-class", classId);

      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      videoRef.current.srcObject = stream;
      videoRef.current.muted = true;
      await videoRef.current.play();

      setIsLive(true);
    } catch (err) {
      console.error(err);
      setError("Camera permission denied or device not found");
    }
  };

  return (
    <>
      <Navbar />

      <div style={{ padding: 30, textAlign: "center" }}>
        <h2>ðŸŽ¥ Teacher Live Class</h2>

        {error && <p style={{ color: "red" }}>{error}</p>}

        <div
          style={{
            width: "80%",
            height: 350,
            background: "#000",
            margin: "20px auto",
            borderRadius: 12
          }}
        >
          <video
            ref={videoRef}
            autoPlay
            playsInline
            style={{ width: "100%", height: "100%", borderRadius: 12 }}
          />
        </div>

        {!isLive ? (
          <button className="primary-btn" onClick={startClass}>
            â–¶ Start Class
          </button>
        ) : (
          <p style={{ color: "green" }}>ðŸ”´ Live</p>
        )}
      </div>
    </>
  );
}
